mapper.insert:

SELECT NVL(MAX(RNO),0)+1 FROM REVIEWBOARD

INSERT INTO REVIEWBOARD
VALUES(
	#{rNo}, #{rTitle}, #{rContent},
	SYSDATE, 0, 0, 1, #{rVisit}, #{rCate}, #{rRate}, #{memberID}, #{placeNo} 
)

INSERT INTO IMGINFO
VALUES( 
	(SELECT NVL(MAX(iNo),0)+1 FROM IMGINFO),
	#{iSaveName}, #{rNo}
)

mapper.update:
UPDATE 	REVIEWBOARD
SET		RISSHOW = 0
WHERE	RNO = #{rNo}

mapper.select:
SELECT 	COUNT(*)
FROM 	GOODCNT
WHERE 	MEMBERID= #{memberID}
AND		RNO		= #{rNo}
AND		PLACENO	= #{placeNo}

mapper.delete:
DELETE 	
FROM 	GOODCNT
WHERE	MEMBERID= #{memberID}
AND		RNO		= #{rNo}		
AND		PLACENO	= #{placeNo}

mapper.select:
SELECT 	COUNT(*)
FROM 	GOODCNT
WHERE	RNO = #{rNo}

SELECT 	COUNT(*)
FROM 	BADCNT
WHERE 	MEMBERID= #{memberID}
AND		RNO		= #{rNo}
AND		PLACENO	= #{placeNo}

mapper.delete:
DELETE 	
FROM 	BADCNT
WHERE	MEMBERID= #{memberID}
AND		RNO		= #{rNo}		
AND		PLACENO	= #{placeNo}

mapper.select:
SELECT 	COUNT(*)
FROM 	BADCNT
